import styled from 'styled-components';
import Header from '../../components/common/header'
import {PageWrap} from '../../components/common/styled'
import Footer from '../../components/common/footer'
import CompanysItem from '../../components/common/companysItem'
import {AiTwotoneHeart} from 'react-icons/ai'
import {FaBookmark} from 'react-icons/fa'
const DetailsCompany = () => {
  return (
    <>
      <Header />
      <PageMarginBox>
        <MainSectionBox>
          <SectionLeft>

          </SectionLeft>
          <SectionRight>
            <Navigation>
              <NavigationTitle>채용보상금</NavigationTitle>
              <NavigationTextBox>
                <NavigationTextList>
                <NavigationText>추천인</NavigationText>
                  <NavigationSubText>500,000원</NavigationSubText>
                </NavigationTextList>
              <NavigationTextList>
                <NavigationText>지원자</NavigationText>
                <NavigationSubText>500,000원</NavigationSubText>
              </NavigationTextList>
              </NavigationTextBox>
              <NavigationBtnBox style={{marginBottom: '10px'}}>
                <NavigationBtn><FaBookmark color={'#36f'} />북마크하기</NavigationBtn>
              </NavigationBtnBox>
              <NavigationBtnBox>
                <NavigationBtn style={{color:'#fff', background:'#36f'}}>지원하기</NavigationBtn>
              </NavigationBtnBox>
            </Navigation>
          </SectionRight>
        </MainSectionBox>
      </PageMarginBox>
      <CompanysListBox>
        <CompanysList>
        <CompanysItem />
          <CompanysItem></CompanysItem>
          <CompanysItem></CompanysItem>
          <CompanysItem></CompanysItem>
          <CompanysItem></CompanysItem>
          <CompanysItem></CompanysItem>
          <CompanysItem></CompanysItem>
          <CompanysItem></CompanysItem>
        </CompanysList>
      </CompanysListBox>
      <Footer />
    </>
  );
}

export default DetailsCompany;

const PageMarginBox = styled(PageWrap)`
  margin: 0 auto;
  padding: 20px 0 80px;
  display: flex;
  flex-direction: row;
  justify-content: center;
`
const MainSectionBox = styled.div`
  width: 100%;
  max-width: 1060px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`
const SectionLeft = styled.div`
  display: inline-block;
  width: calc(100% - 360px);
  height: 500px;
  vertical-align: top;
  background: red;
`
const SectionRight = styled.div`
  width: 340px;
  top: 70px;
`
const Navigation = styled.div`
  position: fixed;
  width: 340px;
  border: 1px solid #e1e2e3;
  background-color: #fff;
  padding: 24px 20px;
`
const NavigationTitle = styled.div`
  font-size: 15px;
  font-weight: 600;
  letter-spacing: normal;
  text-align: left;
  color: #333;
`
const NavigationTextBox = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  overflow: hidden;
  margin: 24px 0;
`
const NavigationTextList = styled.div`
  flex-grow: 1;
`
const NavigationText = styled.div`
  font-size: 14px;
  font-weight: 600;
  color: #999;
  margin-bottom: 8px;
  line-height: 1.2;
`
const NavigationSubText = styled.div`
  font-size: 15px;
  color: #333;
  font-weight: 600;
`
const NavigationBtnBox = styled.div`
  width: 100%;
`
const NavigationBtn = styled.button`
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
  flex-direction: row;
  -ms-flex-wrap: nowrap;
  flex-wrap: nowrap;
  -webkit-box-pack: center;
  justify-content: center;
  align-content: stretch;
  -webkit-box-align: center;
  align-items: center;
  width: 100%;
  height: 50px;
  border-radius: 25px;
  background-color: #fff;
  border: 1px solid #36f;
  font-size: 16px;
  font-weight: 600;
  letter-spacing: normal;
  color: #36f;
  text-align: center;
`
// Company List
const CompanysListBox = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`
const CompanysList = styled.div`
  width: 100%;
  max-width: 1080px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: center;
  `
