import styled from 'styled-components';
import MainPageSections from '../../components/common/mainPageSections'

const MainPage = () => {
  const sections = [
    {
      img:'https://static.wanted.co.kr/images/newintro/001.webp',
      title:'더 나은 커리어를 위한\n' +
        '새로운 기회',
      text:'나에게 딱 맞는 회사를 찾거나\n' +
        '나에게 딱 맞는 회사가 커리어를 제안합니다.'
    }, {
      img:'https://static.wanted.co.kr/images/newintro/002.webp',
      title:'실력있는 사람들과\n' +
        '함께하는 성장',
      text:'강연&밋업에서 이야기를 듣고 공유하며\n' +
        '함께 성장하는 기회를 발견합니다.'
    }, {
      img:'https://static.wanted.co.kr/images/newintro/003.webp',
      title:'원티드와 요즘 직장인이\n' +
        '만드는 행복',
      text:'직장인 행복을 위한 다양한 이벤트에 참여하고,\n' +
        '일상 속 여유를 즐기기도 합니다.'
    },
  ]
  return(
    <MainPageBox>
      <MainPageBackground>
        <MainPageTextBox>
          <MainPageTitle>직장인의 커리어 여정을<br/> 행복하게, 원티드</MainPageTitle>
          <MainPageText>지금 원티드와 커리어 여정을 시작하세요.</MainPageText>
          <MainPageBtn>지금 시작하기</MainPageBtn>
        </MainPageTextBox>
        </MainPageBackground>
      <MainPageSections />
    </MainPageBox>
  );
}

const MainPageBox = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const MainPageBackground = styled.div`
  position: relative;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  width: 100%;
  height: 550px;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: center;
  -ms-flex-pack: center;
  justify-content: center;
  background: 50%/cover no-repeat #eee url(https://static.wanted.co.kr/images/newintro/pc.webp);
`
const MainPageTextBox = styled.div`
  width: 940px;
  -webkit-box-align: center;
  align-items: center;
  -webkit-box-pack: center;
  justify-content: center;
`
const MainPageTitle = styled.div`
  font-size: 48px;
  line-height: 62px;
  margin-bottom: 20px;
  font-weight: 600;
`
const MainPageText = styled.div`
  font-size: 18px;
  line-height: 28px;
  margin-bottom: 30px;
  font-weight: 500;
`
const MainPageBtn = styled.button`
  height: 64px;
  width: 230px;
  border-radius: 32px;
  background-color: #3a68f9;
  color: #fff;
  font-size: 20px;
  line-height: 17px;
  border: 0;
`
export default MainPage;
