import axios from 'axios'



const DetailsCompany = () => {
  const [datas, setDatas] = useState(null)

  useEffect(async () => {
    await axios.get('https://prod.wantedb.shop/positions/1').then(res => setDatas(res.data.result));
    console.log(datas);
  }, []);


  const onClick = () => {
    const move = document.getElementById('moveImg');
    // transform: translate(-100%);
    move.style.transform='translate(-50%)';
  }
  //{datas !== null ? datas.tag.map( (v,i) =>
  //                   <Tages key={i}>#{v}</Tages>
  //                 ): null}
  return (
    <Page>
      <Header />
      <PageMarginBox>
        <MainSectionBox>
          <SectionLeft>
            <ImgContainer>
              <ImgLeftBtnBox onClick={onClick}><MdOutlineArrowBackIos/></ImgLeftBtnBox>
