                <SliderImgBox id='moveImg'>
                <ImgBox className='imgs'>
                  <SliderImg/>
                </ImgBox>
                <ImgBox className='imgs'>
                  <SliderImg />
                </ImgBox>
                <ImgBox className='imgs'>
                  <SliderImg />
                </ImgBox>
              </SliderImgBox>
