import { PageWrap } from "../../components/common/styled"
import Header from "../../components/common/header"
import styled from "styled-components"
import { useState } from "react"
import { useHistory } from "react-router-dom"
import { loginAction } from "../../store/actions/login"
import { useDispatch } from "react-redux"

const LoginPage = () => {

    //외부 모듈
    const histroy = useHistory();
    const dispatch = useDispatch();

    //state 생성
    const [name, setName] = useState('');

    //name 셋팅
    const handleName = (e) => {
        setName(e.target.value);
    }

    //로그인 버튼 클릭
    const handleLogin = () => {

        //벨리데이션
        if (!name) {
            alert("닉네임을 입력해주세요");
            return
        }

        //서버통신 코드 작성

        //redux 넣어주기
        dispatch(
            loginAction({ name: name })
        )

        //페이지 이동
        histroy.push('/home');
    }

    return (
        <PageWrap>
            <Header page="login" />
            <LoginWrap>
                <div style={{ fontSize: '25px', fontWeight: '600', marginBottom: '40px' }}>로그인</div>
                <div style={{ fontSize: '18px', fontWeight: '600', marginBottom: '10px' }}>닉네임</div>
                <InputWrap value={name} onChange={handleName} placeholder="닉네임을 입력해주세요." />
                <LoginButton onClick={handleLogin}>로그인</LoginButton>
            </LoginWrap>
        </PageWrap>
    )
}


const LoginWrap = styled.div`
    position:absolute;
    top:50%;
    left:50%;
    transform:translate(-50%,-50%);

    padding:40px 40px;

    border:1px solid gray;
`;

const InputWrap = styled.input`
    width:300px;
    height:40px;
    padding-left:10px;
    margin-bottom:60px;
`;

const LoginButton = styled.div`

    width:100%;
    padding:14px 0px;


    border-radius:6px;

    background-color:rgb(59,105,246);

    font-size:18px;
    font-weight:600;
    color:white;
    text-align:center;

    cursor: pointer;
`;

export default LoginPage
