import styled from 'styled-components'

const HeaderMenuItem = ({props}) => {
  const lengths = props.texts.length > 8;
  let oneArray;
  let twoArray
  if(lengths){
    oneArray = props.texts.slice(0,8);
    twoArray = props.texts.slice(8,props.texts.length);
    twoArray.push('더보기');
  }
  console.log(oneArray, twoArray)
  return (

    <DropDownMenuBox>
      {lengths ?
        <>
          <MenuItem>
            <MentItemTitle>{props.title}</MentItemTitle>
            {
              oneArray.map(v =>
                <MenuItemText>{v}</MenuItemText>
              )
            }
          </MenuItem>
          <MenuItem>
            <MentItemTitle>s</MentItemTitle>
            {
              twoArray.map(v =>
                <MenuItemText>{v}</MenuItemText>
              )
            }
          </MenuItem>
        </>
        :
        <MenuItem>
          <MentItemTitle>{props.title}</MentItemTitle>
          {
            props.texts.map(v =>
              <MenuItemText>{v}</MenuItemText>
            )
          }
        </MenuItem>
      }
    </DropDownMenuBox>
  )
}
const DropDownMenuBox = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: left;
  height: 625px;
  max-width: 1060px;
  width: 25%;
  height: 100%;
`
const MenuItem = styled.div`
  display: flex;
  flex-direction: column;
  width: 16.666%;
  height: 270px;
  padding: 40px 20px 0 0;
  text-align: left;
`
const MentItemTitle = styled.div`
  position: relative;
  font-size: 17px;
  color: #333;
  line-height: 20px;
  vertical-align: top;
  padding-right: 20px;
  padding-bottom: 15px;
  width: 100%;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
`
const MenuItemText = styled.div`
  position: relative;
  font-size: 13px;
  color: #999;
  padding: 5px 20px 5px 0;
  width: 100%;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
`

export default HeaderMenuItem;
