import styled from 'styled-components'

const HeaderMenuItem = ({props}) => {
  const lengths = props.texts.length > 8;
  const arrays = props.texts.slice(8,props.texts.length);
  console.log(arrays)
  return (

    <DropDownMenuBox>
      {lengths ?
        <MenuItem>
          <MentItemTitle>{props.title}</MentItemTitle>
          {
            props.texts.map(v =>
              <MenuItemText>{v}</MenuItemText>
            )
          }
        </MenuItem>
        :
        <MenuItem>
          <MentItemTitle>{props.title}</MentItemTitle>
          {
            props.texts.map(v =>
              <MenuItemText>{v}</MenuItemText>
            )
          }
        </MenuItem>

      }
    </DropDownMenuBox>
  )
}
const DropDownMenuBox = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  height: 625px;
  max-width: 1060px;
  width: 100%;
  height: 100%;
`
const MenuItem = styled.div`
`
const MentItemTitle = styled.div`
  position: relative;
  font-size: 17px;
  color: #333;
  line-height: 20px;
  vertical-align: top;
  padding-right: 20px;
  padding-bottom: 15px;
  width: 100%;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
`
const MenuItemText = styled.div`
  position: relative;
  font-size: 13px;
  color: #999;
  padding: 5px 20px 5px 0;
  width: 100%;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
`

export default HeaderMenuItem;
