import styled from 'styled-components';
import {HiOutlineMail} from 'react-icons/hi';

const LoginModal = () => {
  return (
    <LoginBox>
      <LoginLogo>wanted</LoginLogo>
      <LoginFormBox>
        <LoginTextBox>
          <LoginTitle>직장인을 위한 <br/>커리어 플랫폼, 원티드!</LoginTitle>
          <LoginText>커리어 성장과 행복을 위한 여정 <br/>지금 원티드에서 시작하세요.</LoginText>
        </LoginTextBox>
        <EmailFormBox>
          <InputLabel>이메일</InputLabel>
          <InputBox>
            <Input type='email' placeholder='이메일을 입력해 주세요'/>
          </InputBox>
          <LoginBtnBox>
            <EmailBtn><HiOutlineMail className='icon'/>이메일로 계속하기</EmailBtn>
          </LoginBtnBox>
          <OrText>or</OrText>
          <NextText>다음 계정으로 계속하기</NextText>
          <IconsBox>

          </IconsBox>
        </EmailFormBox>
      </LoginFormBox>
    </LoginBox>
  );
}

const LoginBox = styled.div`
  position: fixed;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 400px;
  overflow-y: auto;
  max-width: 500px;
  max-height: calc(100vh - 150px);
  z-index: 5000;
  border-radius: 5px;
  background-color: #fff;
  top: 50%;
  left: 50%;
  transform: translate(-50%,-50%);
`
const LoginLogo = styled.div`
  height: 54px;
  padding: 16px 20px;
  position: relative;
  border-bottom: 1px solid #eee;
  color: #333;
  text-align: center;
  font-size: 20px;
  font-weight: 800;
  word-wrap: break-word;
`
const LoginFormBox = styled.div`
  width: 100%;
  padding: 20px;
`
const LoginTextBox = styled.div`
  text-align: center;
  word-break: break-word;
  margin-top: 24px;
  margin-bottom: 40px;
`
const LoginTitle = styled.div`
  line-height: 1.54;
  font-size: 26px;
  font-weight: 600;
  color: #333;
`
const LoginText = styled.div`
  margin-top: 16px;
  line-height: 1.5;
  font-size: 16px;
  font-weight: 400;
  color: #666;
`

const EmailFormBox = styled.div`
  width: 100%;
  padding-bottom: 14px;
`
const InputLabel = styled.label`
  font-size: 14px;
  font-weight: 400;
  color: #767676;
`
const InputBox = styled.div`
  width: 100%;
  margin-top: 11px;
  padding-bottom: 20px;

`
const Input = styled.input`
  width: 100%;
  height: 50px;
  padding-right: 15px;
  padding-left: 15px;
  border-radius: 5px;
  border: 1px solid #e1e2e3;
  background-color: #fff;
  font-size: 15px;
  color: #333;
`

const LoginBtnBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`
const EmailBtn = styled.button`
  display: flex;
  -webkit-box-pack: center;
  justify-content: center;
  -webkit-box-align: center;
  align-items: center;
  width: 100%;
  height: 54px;
  border: 0;
  border-radius: 27px;
  background-color: #36f;
  font-size: 16px;
  font-weight: 600;
  color: #fff;
  cursor: pointer;
  & .icon {
    margin-right: 10px;
    font-size: 22px;
  }
`
const OrText = styled.div`
  font-size: 14px;
  text-align: center;
  display: block;
  content: "or";
  color: #969696;
  font-weight: 500;
  line-height: 1;
  margin: 13px auto;
  white-space: pre;
`
const NextText = styled.div`
  color: #767676;
  margin-bottom: 16px;
  font-size: 14px;
  text-align: center;
`

const IconsBox = styled.div`
`


export default LoginModal;
