              <UserBox>
                <UserText>회원가입/로그인</UserText>
              </UserBox>
              <Line />
              <CompanyBox>
                <CompanyService>기업 서비스</CompanyService>
              </CompanyBox>
            </FlexRightBox>
          </HeaderCenterBox>
        </HeaderBox>
        <DropDownMenuBox>
          <DropDownMenu>
tsetset
          </DropDownMenu>
        </DropDownMenuBox>
      </Headers>
    )
}
const Headers = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
`
const HeaderBox = styled.div`
  display: flex;
  width: 100%;
  height: 50px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #fff;
  box-shadow: 0 1px 0 0 rgb(0 0 0 / 10%);
`
const HeaderCenterBox = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  max-width: 1060px;
  width: 100%;
  height: 100%;

`

// Logo Box(FlexLeft)
const LogoBox = styled.div`
  font-size: 25px;
  line-height: 17px;
  font-weight: 800;
  text-decoration: none solid rgba(51,51,51);
`

// Link Box(Flex Center)
const LinkListBox = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

`
