  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`
const SearchBox = styled.div`
  display: flex;
  justify-content: center;
`
const UserBox = styled.div`
  display: flex;
  justify-content: center;
`
const UserText = styled.div`
  height: 100%;
  padding: 0 10px;
  font-size: 14px;
  color: #333;
  font-weight: 600;
  line-height: 1;
`
const CompanyBox = styled.div`
  display: flex;
  justify-content: center;
`
const Line = styled.div`
  display: block;
  content: "";
  width: 1px;
  height: 10px;
  background-color: #e1e2e3;
  margin: auto 10px;
`
const CompanyService = styled.div`
  font-size: 13px;
  color: #666;
  line-height: 30px;
  height: 30px;
  border: 1px solid #e1e2e3;
  border-radius: 15px;
  padding: 0 10px;
  margin: 0 0 0 15px;
  font-weight: 400;
`

const DropDownMenuBox = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  height: 625px;
`
const DropDownMenu = styled.div`
`

export default Header

